package weekonedaytwo;

public class PrimeNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 10;
		if(n%2 == 0)
		{
			System.out.println( n + " is not a prime number ");
		}
		else
		{
			System.out.println( n + "is  a prime number ");
		}

	}

}
